import { Component, Input, Output, EventEmitter, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Expense } from '../../models/expense.model';
import { ExpenseService } from '../../services/expense.service';

@Component({
  selector: 'app-expense-form',
  templateUrl: './expense-form.component.html',
  styleUrls: ['./expense-form.component.css']
})
export class ExpenseFormComponent implements OnInit {
  @Input() expense: Expense | null = null;
  @Output() onSave = new EventEmitter<void>();
  @Output() onCancel = new EventEmitter<void>();

  expenseForm!: FormGroup;
  categories: string[] = ['Food', 'Transportation', 'Entertainment', 'Bills', 'Shopping', 'Health', 'Other'];
  isEditing: boolean = false;

  constructor(
    private fb: FormBuilder,
    private expenseService: ExpenseService
  ) { }

  ngOnInit(): void {
    this.isEditing = !!this.expense;
    this.initForm();
  }

  initForm(): void {
    const today = new Date().toISOString().split('T')[0];
    
    this.expenseForm = this.fb.group({
      title: [this.expense?.title || '', Validators.required],
      amount: [this.expense?.amount || 0, [Validators.required, Validators.min(0.01)]],
      category: [this.expense?.category || 'Other', Validators.required],
      date: [this.expense ? new Date(this.expense.date).toISOString().split('T')[0] : today, Validators.required],
      description: [this.expense?.description || '']
    });
  }

  onSubmit(): void {
    if (this.expenseForm.valid) {
      const expenseData: Expense = this.expenseForm.value;
      console.log('Submitting expense:', expenseData); // Debug log

      if (this.isEditing && this.expense?._id) {
        this.expenseService.updateExpense(this.expense._id, expenseData).subscribe({
          next: (response) => {
            console.log('Expense updated successfully:', response);
            this.onSave.emit();
          },
          error: (error) => {
            console.error('Error updating expense:', error);
            alert('Error updating expense. Check console for details.');
          }
        });
      } else {
        this.expenseService.createExpense(expenseData).subscribe({
          next: (response) => {
            console.log('Expense created successfully:', response);
            this.onSave.emit();
          },
          error: (error) => {
            console.error('Error creating expense:', error);
            console.error('Full error details:', JSON.stringify(error, null, 2));
            alert('Error creating expense. Check console for details. Make sure backend is running!');
          }
        });
      }
    } else {
      console.log('Form is invalid:', this.expenseForm.errors);
      console.log('Form values:', this.expenseForm.value);
      // Mark all fields as touched to show validation errors
      Object.keys(this.expenseForm.controls).forEach(key => {
        this.expenseForm.get(key)?.markAsTouched();
      });
    }
  }

  cancel(): void {
    this.onCancel.emit();
  }
}


<div class="card">
  <h2>{{ isEditing ? 'Edit Expense' : 'Add New Expense' }}</h2>
  
  <form [formGroup]="expenseForm" (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="title">Title *</label>
      <input 
        type="text" 
        id="title" 
        class="form-control" 
        formControlName="title"
        placeholder="e.g., Grocery Shopping">
      <small class="error" *ngIf="expenseForm.get('title')?.invalid && expenseForm.get('title')?.touched">
        Title is required
      </small>
    </div>

    <div class="form-row">
      <div class="form-group">
        <label for="amount">Amount ($) *</label>
        <input 
          type="number" 
          id="amount" 
          class="form-control" 
          formControlName="amount"
          step="0.01"
          min="0"
          placeholder="0.00">
        <small class="error" *ngIf="expenseForm.get('amount')?.invalid && expenseForm.get('amount')?.touched">
          Please enter a valid amount
        </small>
      </div>

      <div class="form-group">
        <label for="category">Category *</label>
        <select id="category" class="form-control" formControlName="category">
          <option *ngFor="let cat of categories" [value]="cat">{{ cat }}</option>
        </select>
      </div>
    </div>

    <div class="form-group">
      <label for="date">Date *</label>
      <input 
        type="date" 
        id="date" 
        class="form-control" 
        formControlName="date">
    </div>

    <div class="form-group">
      <label for="description">Description (Optional)</label>
      <textarea 
        id="description" 
        class="form-control" 
        formControlName="description"
        rows="3"
        placeholder="Add any additional details..."></textarea>
    </div>

    <div class="form-actions">
      <button type="submit" class="btn btn-success" [disabled]="!expenseForm.valid">
        {{ isEditing ? 'Update Expense' : 'Add Expense' }}
      </button>
      <button type="button" class="btn btn-secondary" (click)="cancel()">
        Cancel
      </button>
    </div>
  </form>
</div>
